// GetDevNodes returns nodeMetadataList
// TODO: get nodes list from smart contract
func GetDevNodes() []node.NodeMetadata {
	nodeMetadataList := make([]node.NodeMetadata, len(nodes))
	for i, m := range nodes {
		nodeMetadataList[i] = *node.NewNodeMetadata(
			m["key"].(string),
			int64(m["votingPower"].(int)),
			m["listenAddr"].(string),
		)
	}
	return nodeMetadataList
}

func GetContractAddressAt(index int) common.Address {
	if index >= len(GenesisContractAddress) {
		return common.Address{}
	}
	return common.HexToAddress(GenesisContractAddress[index])
}

func GetContractAbiByAddress(address string) string {
	// log.Info("Getting abi for address",  "address", address)
	for add, abi := range GenesisContractAbis {
		if strings.EqualFold(add, address) {
			return abi
		}
	}
	panic("abi not found")
}
